import React from "react";
import styles from "./App.module.css";
import Todo from "./components/Todo";
import Filters from "./components/Filters";
import { useTodosQuery } from "./hooks/useTodosQuery";
import { useFilteredTodos } from "./hooks/useFilteredTodos";

function App() {
  const todos = useTodosQuery();
  const { filteredTodos, applyFilters } = useFilteredTodos(todos);
  const handleChangeComplete = (value: boolean) => {
    if (value) {
      applyFilters([{ field: "completed", value }]);
    } else applyFilters([]);
  };
  const handleChangeNotComplete = (value: boolean) => {
    if (!value) {
      applyFilters([{ field: "completed", value }]);
    } else applyFilters([]);
  };

  return (
    <div className={styles.container}>
      <Filters
        handleChangeComplete={handleChangeComplete}
        handleChangeNotComplete={handleChangeNotComplete}
      />
      <hr />
      {filteredTodos.map((todo) => (
        <Todo todo={todo} />
      ))}
    </div>
  );
}

export default App;
