import { useTodosQuery } from "../hooks/useTodosQuery";
import React, { useState } from "react";
import { TFilterConfig, TTodo } from "../types";

// const todos = [
//   { id: 1, completed: true },
//   { id: 2, completed: false },
//   { id: 3, completed: true },
//   { id: 4, completed: false },
//   { id: 5, completed: true },
// ];

// const filterConfig = [
//   { field: "completed", value: [true, true], type: "intersect" },
//   { field: "sdf", value: true, type: "intersect" },
//   { field: "sdhukguhf", value: true, type: "intersect" },
// ];

type applyFiltersProps = {
  todo: TTodo;
  filterConfig: TFilterConfig;
};

type filterProps = {
  todos: TTodo[];
  filterConfig: TFilterConfig;
};

function applyFilters({ todo, filterConfig }: applyFiltersProps) {
  filterConfig.map((filter) => {
    console.log(todo, filter.field);
    return todo[filter.field] === filter.value;
  });
}

export function filter({ todos, filterConfig }: filterProps) {
  return todos.filter((todo) => applyFilters({ todo, filterConfig }));
}
