import { useEffect, useState } from "react";
import { TFilter, TTodo } from "../types";

export function useFilteredTodos(todos: TTodo[]) {
  const [filters, setFilters] = useState<TFilter[]>([]);
  const [filteredTodos, setFilteredTodos] = useState(todos);

  useEffect(() => {
    if (filters.length === 0) setFilteredTodos(todos);
    else {
      setFilteredTodos(
        todos.filter((todo) => todo.completed === filters[0].value)
      );
    }
  }, [filters, todos]);

  const applyFilters = (newFilters: TFilter[]) => setFilters(newFilters);

  return { filteredTodos, applyFilters };
}
