import React from "react";
import { TTodo } from "../types";

type FiltersProps = {
  setTodos: (todos: TTodo[]) => void;
};

export default function Filters({ setTodos }: FiltersProps) {
  const handleChange = (checked: boolean) => {
    setTodos((todos) =>
      todos.filter((todo: TTodo) => todo.completed === checked)
    );
  };
  return (
    <div>
      <h1>Filters</h1>
      <div>
        <h2>Completed</h2>
        <label>
          <input
            type="checkbox"
            onChange={(event) => handleChange(event.target.checked)}
          />
          <span>Completed</span>
        </label>
        <label>
          <input type="checkbox" />
          <span>Not Completed</span>
        </label>
      </div>
    </div>
  );
}
