import { useEffect, useState } from "react";
import { TTodo } from "../types";
import { getTodos } from "../api";

export function useTodosQuery() {
  const [todos, setTodos] = useState<TTodo[]>([]);
  useEffect(() => {
    getTodos().then((data) => {
      setTodos(data);
    });
  }, []);

  return todos;
}
