import React, { useState } from "react";
import styles from "./App.module.css";
import Todo from "./components/Todo";
import Filters from "./components/Filters";
import { useTodosQuery } from "./hooks/useTodosQuery";
import { useFilteredTodos } from "./hooks/useFilteredTodos";
import { TFilter } from "./types";

function App() {
  const filtersInitial = [
    { filterBy: "completed", name: "completed", value: false },
    { filterBy: "completed", name: "not-completed", value: false },
  ];

  const todos = useTodosQuery();
  const [filters, setFilters] = useState<TFilter[]>([]);
  const { filteredTodos, applyFilters } = useFilteredTodos(todos);

  const handleChangeComplete = (
    filterBy: string,
    name: string,
    value: boolean
  ) => {
    if (value) {
      applyFilters([{ field: "completed", value }]);
    } else applyFilters([]);
  };

  return (
    <div className={styles.container}>
      <Filters handleChangeComplete={handleChangeComplete} />
      <hr />
      {filteredTodos.map((todo) => (
        <Todo todo={todo} />
      ))}
    </div>
  );
}

export default App;
