import { useTodosQuery } from "../hooks/useTodosQuery";

// const todos = [
//   { id: 1, completed: true },
//   { id: 2, completed: false },
//   { id: 3, completed: true },
//   { id: 4, completed: false },
//   { id: 5, completed: true },
// ];

// const filterConfig = [
//   { field: "completed", value: [true, true], type: "intersect" },
//   { field: "sdf", value: true, type: "intersect" },
//   { field: "sdhukguhf", value: true, type: "intersect" },
// ];

function applyFilters(todo, filterConfig) {
  filterConfig.map((filter) => {
    return todo[filter.field] === filter.value;
  });
}

export function filter(filterConfig) {
  const todos = useTodosQuery();
  return todos.filter((todo) => applyFilters(todo, filterConfig));
}
