import React, { Dispatch, SetStateAction, useCallback, useState } from "react";
import { TFilter, TFilterConfig } from "../types";

type FiltersProps = {
  filterConfig: TFilterConfig;
  setFilterConfig: React.Dispatch<React.SetStateAction<TFilterConfig>>;
};

export default function Filters({
  filterConfig,
  setFilterConfig,
}: FiltersProps) {
  const completedFilter = filterConfig.filter(
    (filter) => filter.field === "completed"
  )[0];

  const checked = useCallback(
    (type: boolean) => {
      return completedFilter.value.includes(type);
    },
    [completedFilter]
  );

  // Update filter functions
  const handleCompleted = (value: boolean, type: boolean) => {
    // if unckecking
    if (!value) {
      if (completedFilter.value.includes(type)) {
        const index = completedFilter.value.indexOf(type);
        completedFilter.value.splice(index, 1);
      } else {
        completedFilter.value.push(type);
      }
    }
    setFilterConfig([completedFilter]);
  };
  console.log(filterConfig);

  return (
    <div>
      <h1>Filters</h1>
      <div>
        <h2>Completed</h2>
        <label>
          <input
            type="checkbox"
            checked={checked(true)}
            onChange={(event) => handleCompleted(event?.target.checked, true)}
          />
          <span>Completed</span>
        </label>
        <label>
          <input
            type="checkbox"
            checked={checked(false)}
            onChange={(event) => handleCompleted(!event?.target.checked, false)}
          />
          <span>Not Completed</span>
        </label>
      </div>
    </div>
  );
}
